import axios from "../custom-axios/axios";

const libraryService = {

    fetchAuthors: () => {
        return axios.get('authors')
    },

    fetchBooks: () => {
        return axios.get('books')
    },

    fetchCountries: () => {
        return axios.get('countries')
    },

    fetchCategories: () => {
        return axios.get('categories')
            .then(response => console.log(response.data));
    },

    deleteBook: (id) => {
        return axios.delete(`/api/books/delete/${id}`)
            .then(response => console.log(response.data));
    },

    addBook: (name, category, availableCopies, authorId, isInGoodCondition) => {
        return axios.post("/api/books/add", {
            "name" : name,
            "category" : category,
            "availableCopies" : availableCopies,
            "authorId" : authorId,
            "isInGoodCondition" : isInGoodCondition
        })
            .then(response => console.log(response.data));
    },
    editBook: (id, name, category, availableCopies, authorId, isInGoodCondition) => {
        return axios.put(`/api/books/edit/${id}`, {
            "name" : name,
            "category" : category,
            "availableCopies" : availableCopies,
            "authorId" : authorId,
            "isInGoodCondition" : isInGoodCondition
        })
            .then(response => console.log(response.data));
    },
    getBook: (id) => {
        return axios.get(`/api/books/${id}`)
            .then(response => console.log(response.data));
    }

}

export default libraryService;
