import React from "react";
import {Link} from "react-router-dom";

const BookTerm = (props) => {
    return (
        <tr key={props.term.id}>
            <td scope={"col"}>{props.term.name}</td>
            <td scope={"col"}>{props.term.category}</td>
            <td scope={"col"}>{props.term.availableCopies}</td>
            {props.authors.map((author) => {
                if (author.id === props.term.authorIdValue) {
                    return(<td scope={"col"}>{author.name}</td>)
                }
            })}
            {props.term.isInGoodCondition == true ? <td scope={"col"}>In good condition</td>
            : <td scope={"col"}>In bad condition</td>}
            <td scope={"col"} className={"text-right"}>
                <a title={"Delete"} className={"btn btn-danger"}
                   onClick={() => props.onDelete(props.term.id)}>
                    Delete
                </a>
                <Link className={"btn btn-info ml-2"}
                      onClick={() => props.onEdit(props.term.id, props.term.name,
                          props.term.category, props.term.availableCopies,
                          props.term.authorId,
                          props.term.isInGoodCondition)}
                      to={`/books/edit/${props.term.id}`}>
                    Edit
                </Link>
            </td>
        </tr>
    )
}

export default BookTerm;
