import logo from '../../logo.svg';
import './App.css';
import React, {Component} from 'react'
import Authors from '../Authors/authors'
import Books from '../Books/BookList/books'
import libraryService from "../../repository/libraryRepository";
import Header from '../Header/header'
import {BrowserRouter as Router, Redirect, Route} from 'react-router-dom';
import BookAdd from '../Books/BookAdd/bookAdd';

class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
      authors: [],
      books: [],
      countries: []
    }
  }

  render() {
    return (
        // <div>
        //   <Books books = {this.state.books}/>
        // </div>
        <Router>
          <Header/>
          <main>
            <div className="container">
              <Route path={"/authors"} exact render={() =>
                  <Authors authors={this.state.authors}/>}/>
            </div>


            <Route path={"/books/add"} exact render={() =>
                <BookAdd authors={this.state.authors}
                            onAddBook={this.addBook}/>}/>

            {/*<Route path={"/products/edit/:id"} exact render={() =>*/}
            {/*    <BookEdit categories={this.state.categories}*/}
            {/*                 manufacturers={this.state.manufacturers}*/}
            {/*                 onEditBook={this.editBook}*/}
            {/*                 product={this.state.selectedBook}/>}/>*/}

            <div className="container">
              <Route path={"/books"} exact render={() =>
                  <Books books={this.state.books}/>}/>
            </div>
          </main>
        </Router>
    )
  }

  loadAuthors = () => {
    libraryService.fetchAuthors().then((data) => {
      this.setState({
        authors: data.data
      })
    });
  }

  addBook = (name, category, availableCopies, authorId, isInGoodCondition) => {
    libraryService.addBook(name, category, availableCopies, authorId, isInGoodCondition)
        .then(() => {
          this.loadBooks();
        });
  }

  loadBooks = () => {
    libraryService.fetchBooks().then((data) => {
      this.setState({
        books: data.data
      })
    })
  }

  loadCountries = () => {
    libraryService.fetchCountries().then((data) => {
      this.setState({
        countries: data
      })
    })
  }

  componentDidMount() {
    this.loadAuthors();
    this.loadBooks();
    this.loadCountries();
  }

}

export default App;
